import React, { useState, useEffect } from 'react';
import { Button, TextField, Container, Typography, Grid, Card, CardContent, Box, Modal, Backdrop, Fade } from '@mui/material';
import { getPosts, createPost, updatePost, deletePost, uploadImage } from '../api';

const PostManager = () => {
    const [posts, setPosts] = useState([]);
    const [newPost, setNewPost] = useState({ title: '', content: '', imageUrl: '' });
    const [image, setImage] = useState(null);
    const [editingPost, setEditingPost] = useState(null); // Lưu bài viết đang chỉnh sửa
    const [errors, setErrors] = useState({ title: '', content: '' });
    const [open, setOpen] = useState(false); // Quản lý modal

    useEffect(() => {
        loadPosts();
    }, []);

    const loadPosts = async () => {
        try {
            const response = await getPosts();
            setPosts(response.data);
        } catch (error) {
            console.error('Error fetching posts:', error);
        }
    };

    const validate = () => {
        const newErrors = { title: '', content: '' };
        if (!editingPost.title) {  // Kiểm tra dữ liệu trong `editingPost`
            newErrors.title = 'Title is required';
        }
        if (!editingPost.content) {  // Kiểm tra dữ liệu trong `editingPost`
            newErrors.content = 'Content is required';
        }
        setErrors(newErrors);
        return Object.values(newErrors).every((error) => error === '');
    };

    const handleCreatePost = async () => {
        if (validate()) {
            try {
                const post = { ...newPost };
                if (image) {
                    const imageUrl = await handleUploadImage(image);
                    post.imageUrl = imageUrl;
                }
                await createPost(post);
                setPosts([...posts, post]);
                setNewPost({ title: '', content: '', imageUrl: '' });
                setImage(null);
                setErrors({ title: '', content: '' });
            } catch (error) {
                console.error('Error creating post:', error);
            }
        }
    };

    const handleUploadImage = async (file) => {
        try {
            const formData = new FormData();
            formData.append('file', file);
            const response = await uploadImage(formData);
            return response.data;
        } catch (error) {
            console.error('Error uploading image:', error);
        }
    };

    const handleImageChange = (e) => {
        setImage(e.target.files[0]);
    };

    const handleDeletePost = async (id) => {
        try {
            await deletePost(id);
            setPosts(posts.filter(post => post.id !== id));
        } catch (error) {
            console.error('Error deleting post:', error);
        }
    };

    // Mở Modal và gán bài viết cần chỉnh sửa vào `editingPost`
    const handleEditPost = (post) => {
        setEditingPost({ ...post });  // Sao chép dữ liệu bài viết vào `editingPost`
        setOpen(true);  // Mở Modal
    };

    const handleCloseModal = () => {
        setOpen(false);
        setEditingPost(null); // Reset `editingPost` khi đóng Modal
    };

    // Cập nhật bài viết
    const handleUpdatePost = async () => {
        if (validate()) {
            try {
                const updatedPost = { ...editingPost };
                if (image) {
                    const imageUrl = await handleUploadImage(image);
                    updatedPost.imageUrl = imageUrl;
                }
                await updatePost(editingPost.id, updatedPost);
                setPosts(posts.map((p) => (p.id === editingPost.id ? updatedPost : p)));
                handleCloseModal();
                setImage(null);
                setErrors({ title: '', content: '' });
            } catch (error) {
                console.error('Error updating post:', error);
            }
        }
    };

    return (
        <Container maxWidth="md">
            {/* Form tạo bài viết mới */}
            <Box mb={3}>
                <TextField
                    label="Title"
                    variant="outlined"
                    fullWidth
                    value={newPost.title}
                    onChange={(e) => setNewPost({ ...newPost, title: e.target.value })}
                    margin="normal"
                    error={!!errors.title}
                    helperText={errors.title}
                />
                <TextField
                    label="Content"
                    variant="outlined"
                    fullWidth
                    multiline
                    rows={4}
                    value={newPost.content}
                    onChange={(e) => setNewPost({ ...newPost, content: e.target.value })}
                    margin="normal"
                    error={!!errors.content}
                    helperText={errors.content}
                />
                <input type="file" onChange={handleImageChange} accept="image/*" />
                <Button variant="contained" color="primary" onClick={handleCreatePost}>
                    Create Post
                </Button>
            </Box>

            {/* Danh sách bài viết */}
            <Grid container spacing={3}>
                {posts.map((post) => (
                    <Grid item xs={12} sm={6} md={4} key={post.id}>
                        <Card>
                            <CardContent>
                                <Typography variant="h6">{post.title}</Typography>
                                <Typography variant="body2" color="textSecondary" paragraph>
                                    {post.content}
                                </Typography>
                                {post.imageUrl && (
                                    <img src="data:image/png;base64," alt="Post" style={{ width: '100%', maxWidth: '100%' }} />
                                )}
                                <Button variant="outlined" color="secondary" onClick={() => handleDeletePost(post.id)}>
                                    Delete
                                </Button>
                                <Button variant="outlined" color="primary" onClick={() => handleEditPost(post)}>
                                    Edit
                                </Button>
                            </CardContent>
                        </Card>
                    </Grid>
                ))}
            </Grid>

            {/* Modal chỉnh sửa bài viết */}
            <Modal open={open} onClose={handleCloseModal} closeAfterTransition BackdropComponent={Backdrop} BackdropProps={{ timeout: 500 }}>
                <Fade in={open}>
                    <Box sx={{ position: 'absolute', top: '50%', left: '50%', transform: 'translate(-50%, -50%)', width: 400, bgcolor: 'background.paper', boxShadow: 24, p: 4 }}>
                        <Typography variant="h6">Edit Post</Typography>
                        <TextField
                            label="Title"
                            variant="outlined"
                            fullWidth
                            value={editingPost?.title || ''}
                            onChange={(e) => setEditingPost({ ...editingPost, title: e.target.value })}
                            margin="normal"
                            error={!!errors.title}
                            helperText={errors.title}
                        />
                        <TextField
                            label="Content"
                            variant="outlined"
                            fullWidth
                            multiline
                            rows={4}
                            value={editingPost?.content || ''}
                            onChange={(e) => setEditingPost({ ...editingPost, content: e.target.value })}
                            margin="normal"
                            error={!!errors.content}
                            helperText={errors.content}
                        />
                        <Button variant="contained" color="primary" onClick={handleUpdatePost} sx={{ mr: 1 }}>
                            Update Post
                        </Button>
                        <Button variant="outlined" onClick={handleCloseModal}>
                            Cancel
                        </Button>
                    </Box>
                </Fade>
            </Modal>
        </Container>
    );
};

export default PostManager;
