import React, { useState, useEffect } from 'react';
import { Button, TextField, Container, Typography, Grid, Card, CardContent, Box } from '@mui/material';
import { getPosts, createPost, updatePost, deletePost, uploadImage } from './api';

const PostManager = () => {
    const [posts, setPosts] = useState([]);
    const [newPost, setNewPost] = useState({ title: '', content: '', imageUrl: '' });
    const [image, setImage] = useState(null);
    const [editingPost, setEditingPost] = useState(null);

    // Load danh sách bài viết khi component mount
    useEffect(() => {
        loadPosts();
    }, []);

    // Hàm tải danh sách bài viết
    const loadPosts = async () => {
        try {
            const response = await getPosts();
            setPosts(response.data);
        } catch (error) {
            console.error('Error fetching posts:', error);
        }
    };

    // Tạo bài viết mới
    const handleCreatePost = async () => {
        try {
            const post = { ...newPost };
            if (image) {
                const imageUrl = await handleUploadImage(image); // Lấy URL ảnh từ backend
                post.imageUrl = imageUrl; // Gắn URL ảnh vào bài viết
            }
            await createPost(post); // Gửi bài viết kèm URL ảnh lên backend
            setPosts([...posts, post]);
            setNewPost({ title: '', content: '', imageUrl: '' });
            setImage(null);
        } catch (error) {
            console.error('Error creating post:', error);
        }
    };

    // Upload ảnh lên backend
    const handleUploadImage = async (file) => {
        try {
            const formData = new FormData();
            formData.append('file', file);
            const response = await uploadImage(formData);
            return response.data; // Trả về URL ảnh
        } catch (error) {
            console.error('Error uploading image:', error);
        }
    };

    // Lấy ảnh từ file input
    const handleImageChange = (e) => {
        setImage(e.target.files[0]);
    };

    // Xóa bài viết
    const handleDeletePost = async (id) => {
        try {
            await deletePost(id);  // Gọi API để xóa bài viết từ backend
            setPosts(posts.filter(post => post.id !== id));  // Cập nhật danh sách bài viết trong state
        } catch (error) {
            console.error('Error deleting post:', error);
        }
    };

    // Chỉnh sửa bài viết
    const handleEditPost = (post) => {
        setEditingPost(post);  // Cập nhật bài viết cần chỉnh sửa
    };

    // Cập nhật bài viết
    const handleUpdatePost = async (post) => {
        try {
            const updatedPost = { ...post };
            if (image) {
                const imageUrl = await handleUploadImage(image);  // Lấy URL ảnh từ backend
                updatedPost.imageUrl = imageUrl; // Gắn URL ảnh vào bài viết
            }
            await updatePost(post.id, updatedPost);  // Gửi bài viết cập nhật lên backend
            setPosts(posts.map(p => p.id === post.id ? updatedPost : p));  // Cập nhật bài viết trong danh sách
            setEditingPost(null);  // Đóng form chỉnh sửa
            setImage(null);  // Xóa ảnh đã chọn
        } catch (error) {
            console.error('Error updating post:', error);
        }
    };

    return (
        <Container maxWidth="md">
            <Typography variant="h4" gutterBottom>
                Post Manager
            </Typography>

            {/* Form tạo bài viết mới */}
            <Box mb={3}>
                <TextField
                    label="Title"
                    variant="outlined"
                    fullWidth
                    value={newPost.title}
                    onChange={(e) => setNewPost({ ...newPost, title: e.target.value })}
                    margin="normal"
                />
                <TextField
                    label="Content"
                    variant="outlined"
                    fullWidth
                    multiline
                    rows={4}
                    value={newPost.content}
                    onChange={(e) => setNewPost({ ...newPost, content: e.target.value })}
                    margin="normal"
                />

                {/* Upload ảnh */}
                <input
                    type="file"
                    onChange={handleImageChange}
                    accept="image/*"
                />

                <Button variant="contained" color="primary" onClick={handleCreatePost}>
                    Create Post
                </Button>
            </Box>

            {/* Danh sách bài viết */}
            <Grid container spacing={3}>
                {posts.map((post) => (
                    <Grid item xs={12} sm={6} md={4} key={post.id}>
                        <Card>
                            <CardContent>
                                <Typography variant="h6">{post.title}</Typography>
                                <Typography variant="body2" color="textSecondary" paragraph>
                                    {post.content}
                                </Typography>
                                {post.imageUrl && (
                                    <img src={`../../../Backend${post.imageUrl}`} alt="Post" width="100%" />
                                )}
                                <Button
                                    variant="outlined"
                                    color="secondary"
                                    onClick={() => handleDeletePost(post.id)}  // Xóa bài viết
                                    sx={{ mr: 1 }}
                                >
                                    Delete
                                </Button>
                                <Button
                                    variant="outlined"
                                    color="primary"
                                    onClick={() => handleEditPost(post)}  // Chỉnh sửa bài viết
                                >
                                    Edit
                                </Button>
                            </CardContent>
                        </Card>
                    </Grid>
                ))}
            </Grid>

            {/* Form chỉnh sửa bài viết */}
            {editingPost && (
                <Box mt={3}>
                    <Typography variant="h6">Edit Post</Typography>
                    <TextField
                        label="Title"
                        variant="outlined"
                        fullWidth
                        value={editingPost.title}
                        onChange={(e) => setEditingPost({ ...editingPost, title: e.target.value })}
                        margin="normal"
                    />
                    <TextField
                        label="Content"
                        variant="outlined"
                        fullWidth
                        multiline
                        rows={4}
                        value={editingPost.content}
                        onChange={(e) => setEditingPost({ ...editingPost, content: e.target.value })}
                        margin="normal"
                    />
                    <Button
                        variant="contained"
                        color="primary"
                        onClick={() => handleUpdatePost(editingPost)}  // Cập nhật bài viết
                        sx={{ mr: 1 }}
                    >
                        Update Post
                    </Button>
                    <Button variant="outlined" onClick={() => setEditingPost(null)}>
                        Cancel
                    </Button>
                </Box>
            )}
        </Container>
    );
};

export default PostManager;
